edit.action:
//-- mod : Auto Prune Logs --------------------------------------------------------
//-- add
$prune_log_next_time = (!empty($config['prune_log_next_time'])) ? $config['prune_log_next_time'] : 0;
$prune_log_day = (!empty($config['prune_log_day'])) ? $config['prune_log_next_time'] : 0;

if (time() > $prune_log_next_time)
{
	if (!empty($config['prune_admin_log_day']))
	{
		$sql = 'DELETE FROM ' . LOG_TABLE . '
			WHERE log_type = 0
			AND log_time < ' . (time() - (int) $config['prune_admin_log_day'] * 86400);
		$db->sql_query($sql);
	}

	if (!empty($config['prune_mod_log_day']))
	{
		$sql = 'DELETE FROM ' . LOG_TABLE . '
			WHERE log_type = 1
			AND log_time < ' . (time() - (int) $config['prune_mod_log_day'] * 86400);
		$db->sql_query($sql);
	}

	if (!empty($config['prune_log_next_time']))
	{
		set_config('prune_log_next_time', (time() + (int) $prune_log_day * 86400), 1);
	}
}
//-- end : Auto Prune Logs --------------------------------------------------------

//-- mod : Auto Prune Logs --------------------------------------------------------
//-- add
						'legend3'				=> 'ACP_CAT_MAINTENANCE',
